import React, { useState } from "react";
import API from "../api";

function TicketForm({ onCreated }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [priority, setPriority] = useState("");
  const [loading, setLoading] = useState(false);

  const classify = async () => {
    if (!description) return;

    setLoading(true);

    try {
      const res = await API.post("tickets/classify/", {
        description,
      });

      setCategory(res.data.suggested_category);
      setPriority(res.data.suggested_priority);
    } catch (err) {
      console.error(err);
    }

    setLoading(false);
  };

  const submit = async () => {
    try {
      await API.post("tickets/", {
        title,
        description,
        category,
        priority,
      });

      setTitle("");
      setDescription("");
      setCategory("");
      setPriority("");

      onCreated();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ marginBottom: 30 }}>
      <h2>Create Ticket</h2>

      <input
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <br />

      <textarea
        placeholder="Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <br />

      <button onClick={classify}>
        {loading ? "Classifying..." : "AI Suggest"}
      </button>

      <br />

      <input
        placeholder="Category"
        value={category}
        onChange={(e) => setCategory(e.target.value)}
      />

      <input
        placeholder="Priority"
        value={priority}
        onChange={(e) => setPriority(e.target.value)}
      />

      <br />

      <button onClick={submit}>Submit Ticket</button>
    </div>
  );
}

export default TicketForm;
